    {
      "target": {
        "selector": ".line"
      },
      "enter": {
        "haptic": {
          "durations": [0, 1000, 0, 1000],
          "repeatInterval": 1000
        },
        "notes": "longer, steady vibration to indicate shape lines"
      },
      "details": {
      },
      "activate": {
      },
      "exit": {
      }
    },
    {
      "target": {
        "selector": ".right_angle"
      },
      "enter": {
        "haptic": {
          "durations": [0, 75, 75, 75, 75, 75, 225],
          "repeatInterval": 75
        },
        "notes": "sharp, steady vibration to indicate right angle"
      }
    },
    {
      "target": {
        "selector": "#angle-w"
      },
      "enter": {
        "haptic": {
          "durations": [0, 125, 125, 125, 125, 125, 125, 125],
          "repeatInterval": 125
        },
        "notes": "sharp, snappy vibration, heavy on the first beat, to indicate acute angle"
      }
    },
    {
      "target": {
        "selector": "#angle-x"
      },
      "enter": {
        "haptic": {
          "durations": [0, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50, 50],
          "repeatInterval": 50
        },
        "notes": "rapid buzzing vibration to indicate obtuse angle"
      }
    },
    {
      "target": {
        "selector": "#angle-y, #angle-z"
      },
      "enter": {
        "haptic": {
          "durations": [0, 75, 75, 75, 75, 75, 225],
          "repeatInterval": 75
        },
        "notes": "sharp, steady vibration to indicate right angle"
      }
    }
  ],
  "status": {
    "features": [
      "haptic",
      "announcement"
    ]
  }
