{
  "version": {
    "document": "1.0.1",
    "jim": "0.4.1"
  },
  "datasets": [
    {
      "title": "Right Triangle",
      "description": "A right triangle with vertices A, B, and C",
      "series": [],
      "items": {
        "shape": {
          "right_triangle": {
            "label": "Right Triangle"
          },
          "side_A_C": {
            "label": "Leg A-C"
          },
          "side_B_C": {
            "label": "Leg B-C"
          },
          "side_A_B": {
            "label": "Hypotenuse A-B"
          }
        },
        "vertices": {
          "angle_a": {
            "label": "Angle A",
            "description": "Intersection of A-B and A-C"
          },
          "angle_b": {
            "label": "Angle B",
            "description": "Intersection of A-B and B-C"
          },
          "angle_c": {
            "label": "Angle C",
            "description": "Right angle intersection of A-C and B-C"
          }
        },
        "markers": {
          "right_angle": {
            "label": "Right angle"
          }
        }
      }
    }
  ],
  "selectors": {
    "triangle": {
      "dom": "#triangle",
      "json": "$.datasets[0].items.shape.right_triangle.label",
      "note": "The triangle shape"
    },

    "side-A-C": {
      "dom": "#side-A-C",
      "json": "$.datasets[0].items.shape.side_A_C.label",
      "note": "Side A-C"
    },

    "side-B-C": {
      "dom": "#side-B-C",
      "json": "$.datasets[0].items.shape.side_B_C.label",
      "note": "Side B-C"
    },

    "side-A-B": {
      "dom": "#side-A-B",
