      "json": "$.datasets[0].items.vertices.angle_a.label",
      "note": "Vertex A"
    },

    "vertex-B": {
      "dom": "#vertex-B",
      "json": "$.datasets[0].items.vertices.angle_b.label",
      "note": "Vertex B"
    },

    "vertex-C": {
