svg.metadata:
{
  "version": {
    "jim": "0.4.2"
  },
  "datasets": [
    {
      "title": "Triangle with sides in ratio 3:5:6",
      "subtitle": "Polygon geometry problem",
      "description": "A triangle with sides in the ratio 3:5:6, rotated so the smallest angle is at the top.",
      "source": {
        "url": "https://inclusioproject.com/graphics/exemplars/Triangle with sides in ratio 3:5:6.svg",
        "name": "Inclusio Project accessible graphic exemplar: Triangle with sides in ratio 3:5:6"
      },
      "items": {
        "shape": {
          "edge_0": {
            "label": "line-AB",
            "measurement": "5"
          },
          "edge_1": {
            "label": "line-BC",
            "measurement": "6"
          },
          "edge_2": {
            "label": "line-CA",
            "measurement": "3"
          }
        },
        "angles": {},
        "markers": {}
      }
    }
  ],
  "selectors": {
    "title": {
      "dom": "#diagram-title",
      "json": "$.datasets[0].title"
    },
    "edge_AB": {
      "dom": "#edge-AB",
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "edge_BC": {
      "dom": "#edge-BC",
      "json": "$.datasets[0].items.shape.edge_1"
    },
    "edge_CA": {
      "dom": "#edge-CA",
      "json": "$.datasets[0].items.shape.edge_2"
    },
    "label_AB": {
      "dom": "#label-AB", 
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "label_BC": {
      "dom": "#label-BC", 
      "json": "$.datasets[0].items.shape.edge_1"
    },
    "label_CA": {
      "dom": "#label-CA", 
      "json": "$.datasets[0].items.shape.edge_2"
    }
  },
  "behaviors": [
    {
      "target": {
        "selector": ".line"
      },
      "enter": {
        "haptic": {
          "durations": [0, 300, 100],
          "repeatInterval": 0
        },
        "announcement": {
          "name": "compose({target.id}, .label)"
        }
      }
    }
  ],
  "status": {
    "features": [
      "haptic",
      "announcement"
    ]
  }
