svg.metadata:
{
  "version": {
    "jim": "0.4.2"
  },
  "datasets": [
    {
      "title": "Square ABCD",
      "subtitle": "Polygon geometry problem",
      "description": "A square with vertices A, B, C, D and each side measuring 4 cm.",
      "source": {
        "url": "https://inclusioproject.com/graphics/exemplars/Square ABCD.svg",
        "name": "Inclusio Project accessible graphic exemplar: Square ABCD"
      },
      "items": {
        "shape": {
          "edge_0": {
            "label": "line-AB",
            "measurement": "4 cm"
          },
          "edge_1": {
            "label": "line-BC",
            "measurement": "4 cm"
          },
          "edge_2": {
            "label": "line-CD",
            "measurement": "4 cm"
          },
          "edge_3": {
            "label": "line-DA",
            "measurement": "4 cm"
          }
        },
        "angles": {},
        "markers": {}
      }
    }
  ],
  "selectors": {
    "diagramTitle": {
      "dom": "#diagram-title",
      "json": "$.datasets[0].title"
    },
    "lineAB": {
      "dom": "#edge-AB",
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "labelLineAB": {
      "dom": "#label-AB",
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "lineBC": {
      "dom": "#edge-BC",
      "json": "$.datasets[0].items.shape.edge_1"
    },
    "labelLineBC": {
      "dom": "#label-BC",
      "json": "$.datasets[0].items.shape.edge_1"
    },
    "lineCD": {
      "dom": "#edge-CD",
      "json": "$.datasets[0].items.shape.edge_2"
    },
    "labelLineCD": {
      "dom": "#label-CD",
      "json": "$.datasets[0].items.shape.edge_2"
    },
    "lineDA": {
      "dom": "#edge-DA",
      "json": "$.datasets[0].items.shape.edge_3"
    },
    "labelLineDA": {
      "dom": "#label-DA",
      "json": "$.datasets[0].items.shape.edge_3"
    }
  },
  "behaviors": [
    {
      "target": {
        "selector": ".line"
      },
      "enter": {
        "haptic": {
          "durations": [0, 300, 100],
          "repeatInterval": 0
        },
        "announcement": {
          "name": "compose({target.id}, .label)"
        }      
      }
    }
  ],
  "status": {
    "features": [
      "haptic",
      "announcement"
    ]
  }
}
