{
  "version": {
    "jim": "0.4.2"
  },
  "datasets": [
    {
      "title": "Hexagon ABCDEF",
      "subtitle": "Polygon geometry problem",
      "description": "A regular hexagon with vertices A, B, C, D, E, F and equal side lengths of 2 cm.",
      "source": {
        "url": "https://inclusioproject.com/graphics/exemplars/Hexagon ABCDEF.svg",
        "name": "Inclusio Project accessible graphic exemplar: Hexagon ABCDEF"
      },
      "items": {
        "shape": {
          "edge_0": {
            "label": "line-AB",
            "measurement": "2 cm"
          },
          "edge_1": {
            "label": "line-BC",
            "measurement": "2 cm"
          },
          "edge_2": {
            "label": "line-CD",
            "measurement": "2 cm"
          },
          "edge_3": {
            "label": "line-DE",
            "measurement": "2 cm"
          },
          "edge_4": {
            "label": "line-EF",
            "measurement": "2 cm"
          },
          "edge_5": {
            "label": "line-FA",
            "measurement": "2 cm"
          }
        },
        "angles": {},
        "markers": {}
      }
    }
  ],
  "selectors": {
    "diagramTitle": {
      "dom": "#diagram-title",
      "json": "$.datasets[0].title"
    },
    "lineAB": {
      "dom": "#edge-AB",
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "labelLineAB": {
      "dom": "#label-AB",
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "lineBC": {
