      "json": "$.datasets[0].items.shape.edge_1"
    },
    "labelLineBC": {
      "dom": "#label-BC",
      "json": "$.datasets[0].items.shape.edge_1"
    },
    "lineCD": {
      "dom": "#edge-CD",
      "json": "$.datasets[0].items.shape.edge_2"
    },
    "labelLineCD": {
      "dom": "#label-CD",
      "json": "$.datasets[0].items.shape.edge_2"
    },
    "lineDE": {
