      "json": "$.datasets[0].items.shape.edge_5"
    }
  },
  "behaviors": [
    {
      "target": {
        "selector": ".line"
      },
      "enter": {
        "haptic": {
          "durations": [0, 300, 100],
          "repeatInterval": 0
        },
        "announcement": {
          "name": "compose({target.id}, .label)"
        }      
      }
    }
  ],
  "status": {
    "features": [
      "haptic",
      "announcement"
    ]
  }
