svg.metadata:
{
  "version": {
    "jim": "0.4.2"
  },
  "datasets": [
    {
      "title": "Concave Shape with Right Angle",
      "subtitle": "Polygon geometry problem",
      "description": "A concave shape with a right angle at vertex B, formed by segments AB and BC.",
      "source": {
        "url": "https://inclusioproject.com/graphics/exemplars/Concave Shape with Right Angle.svg",
        "name": "Inclusio Project accessible graphic exemplar: Concave Shape with Right Angle"
      },
      "items": {
        "shape": {
          "edge_0": {
            "label": "line-AB",
            "measurement": "AB"
          },
          "edge_1": {
            "label": "line-BC",
            "measurement": "BC"
          }
        },
        "angles": {
          "angle_0": {
            "label": "angle-B",
            "description": "Intersection of edges at angle-B - right",
            "measurement": "90"
          }
        },
        "markers": {}
      }
    }
  ],
  "selectors": {
    "diagramTitle": {
      "dom": "#diagram-title",
      "json": "$.datasets[0].title"
    },
    "lineAB": {
      "dom": "#edge-AB",
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "labelLineAB": {
      "dom": "#label-AB",
      "json": "$.datasets[0].items.shape.edge_0"
    },
    "lineBC": {
      "dom": "#edge-BC",
      "json": "$.datasets[0].items.shape.edge_1"
    },
    "labelLineBC": {
      "dom": "#label-BC",
      "json": "$.datasets[0].items.shape.edge_1"
    },
    "angleB": {
      "dom": "#angle-B",
      "json": "$.datasets[0].items.angles.angle_0"
    },
    "labelAngleB": {
      "dom": "#label-angle-B",
      "json": "$.datasets[0].items.angles.angle_0"
    }
  },
  "behaviors": [
    {
      "target": {
        "selector": ".line"
      },
      "enter": {
        "haptic": {
          "durations": [0, 300, 100],
          "repeatInterval": 0
        },
        "announcement": {
          "name": "compose({target.id}, .label)"
        }
      }
    },
    {
      "target": {
        "selector": "#angle-B"
      },
      "enter": {  
        "haptic": {
          "durations": [0, 100, 100],
          "repeatInterval": 100
        }
      }
    }
  ],
  "status": {
    "features": [
      "haptic",
      "announcement"
    ]
  }
}
