svg.metadata:
{
  "jim": {
    "version": {
      "jim": "1.1.0"
    },
    "datasets": [
      {
        "title": "Enveloped Form Test",
        "description": "Tests enveloped JIM form with earcon and JSONPath targets",
        "facets": {
          "x": { "type": "categorical", "role": "label" },
          "y": { "type": "quantitative", "role": "dependent" }
        },
        "series": [
          {
            "name": "Test Series",
            "records": [
              { "x": "Item A", "y": 10 },
              { "x": "Item B", "y": 20 }
            ]
          }
        ]
      }
    ],
    "selectors": {
      "itemA": {
        "dom": "#item-a",
        "json": "$.jim.datasets[0].series[0].records[0]"
      },
      "itemB": {
        "dom": "#item-b",
        "json": "$.jim.datasets[0].series[0].records[1]"
      }
    },
    "behaviors": [
      {
        "target": {
          "selector": "$.jim.selectors.itemA"
        },
        "enter": {
          "haptic": {
            "durations": [0, 125, 125, 125, 125, 125, 125, 125],
            "repeatInterval": 125
          },
          "audio": {
            "earcon": "PewPew",
            "repeat": "none"
          }
        }
      },
      {
        "target": {
          "selector": "$.jim.selectors.itemB"
        },
        "enter": {
          "haptic": {
            "durations": [0, 125, 125, 125, 125, 125, 125, 125],
            "repeatInterval": 125
          },
          "audio": {
            "earcon": "Click",
            "repeat": "none"
          }
        }
      }
    ]
  }
}
